CREATE TABLE IF NOT EXISTS `blog_articles` (
  `article_id` int(11) NOT NULL COMMENT '文章id',
  `article_title` varchar(100) DEFAULT '' COMMENT '文章标题',
  `article_thumb` varchar(255) DEFAULT '' COMMENT '文章缩略图',
  `article_content` text COMMENT '文章内容',
  `article_author` char(50) DEFAULT '' COMMENT '文章作者',
  `article_tags` varchar(100) DEFAULT '' COMMENT '文章标签',
  `article_views` int(11) DEFAULT '0' COMMENT '文章查看次数',
  `article_addtime` int(11) DEFAULT '0' COMMENT '文章发布时间',
  `article_categoryid` int(11) DEFAULT '0' COMMENT '文章分类id'
) ENGINE=MyISAM AUTO_INCREMENT=14 DEFAULT CHARSET=utf8 COMMENT='文章表';

